import React from 'react';
import {NavLink} from "react-router-dom";

function Nav() {

    const navStyle ={
        padding:"20px",
        backgroundColor:"darkred",
    }

    const linkStyle = {
        color: "white",
        fontweight:"bold",
        marginRight:"20px",

    }

    return(
        <div style={navStyle}>
        <nav>
            {/* for indv active styling : activeStyle={{fontSize: "35px"}}*/}

            <NavLink style={linkStyle} exact to={"/"}>Store</NavLink>
            <NavLink style={linkStyle} exact to={"/cart"}>Cart</NavLink>
            <NavLink style={linkStyle} exact to={"/admin"}>Admin</NavLink>
            {/*<NavLink exact to={"/items"}>Items</NavLink>*/}
        </nav>
        </div>
    );
}

export default Nav;
