import React from 'react';
import Fire from "../fire";


function Cart() {

    const [items, setItems] = React.useState([]);


    const db = Fire.firestore();

    React.useEffect(() => {
        let newItems = [];

        db.collection("items").get().then(function (snapshot) {
            snapshot.forEach(function (doc) {
                const obj = doc.data();
                let item = {
                    id: doc.id,
                    name: obj.name,
                    stock: obj.stock,
                    price: obj.price,
                    img: obj.img
                };
                newItems.push(item);
            });
            setItems(newItems)
        });


    }, [db]);





    const itemEle = items.map((itm, idx) =>
        <div  key={idx} >
            <div>
               <span style={{fontWeight:"bold"}}>{itm.name} </span>
                Price: {itm.price}
            </div>

        </div>
    );




    const itemStyle = {
        display: "flex",
        flexDirection:"column",
      textAlign:"left"

    }

    const itemWrap = {
        marginLeft:"auto",
        marginRight:"auto",
        display: "block",
        width:"50%",
    }



    return(
        <div style={itemWrap}>
            <h1>Your Cart</h1>
            <div style={itemStyle} >
                {itemEle}
            </div>
        </div>
    );
}

export default Cart;
